import{_ as t,o as e,c as d,a as l}from"./app.a2d80bcf.js";const _=JSON.parse('{"title":"HTML5介绍","description":"","frontmatter":{},"headers":[{"level":2,"title":"新特性","slug":"新特性","link":"#新特性","children":[{"level":3,"title":"多媒体","slug":"多媒体","link":"#多媒体","children":[]},{"level":3,"title":"表单","slug":"表单","link":"#表单","children":[]},{"level":3,"title":"语义和结构","slug":"语义和结构","link":"#语义和结构","children":[]}]}],"relativePath":"html/HTML5/html5.md","lastUpdated":1675852229000}'),a={name:"html/HTML5/html5.md"},r=l('<h1 id="html5介绍" tabindex="-1">HTML5介绍 <a class="header-anchor" href="#html5介绍" aria-hidden="true">#</a></h1><p>为了更好地处理今天的互联网应用，HTML5添加了很多新元素及功能，比如： 图形的绘制，多媒体内容，更好的页面结构，更好的形式 处理，和几个api拖放元素，定位，包括网页 应用程序缓存，存储，网络工作者，等。</p><h2 id="新特性" tabindex="-1">新特性 <a class="header-anchor" href="#新特性" aria-hidden="true">#</a></h2><blockquote><p>HTML5将Web带入一个成熟的应用平台，在这个平台上，视频、音频、图像、动画以及与设备的交互都进行了规范。</p></blockquote><h3 id="多媒体" tabindex="-1">多媒体 <a class="header-anchor" href="#多媒体" aria-hidden="true">#</a></h3><table><thead><tr><th style="text-align:center;">标签</th><th style="text-align:center;">描述</th></tr></thead><tbody><tr><td style="text-align:center;"><code>&lt;audio&gt;</code></td><td style="text-align:center;">定义音频内容</td></tr><tr><td style="text-align:center;"><code>&lt;video&gt;</code></td><td style="text-align:center;">定义视频内容（video或者movie）</td></tr><tr><td style="text-align:center;"><code>&lt;source&gt;</code></td><td style="text-align:center;">定义多媒体资源<code>&lt;video&gt; &lt;audio&gt;</code></td></tr><tr><td style="text-align:center;"><code>&lt;embed&gt;</code></td><td style="text-align:center;">定义嵌入的内容，比如插件</td></tr><tr><td style="text-align:center;"><code>&lt;track&gt;</code></td><td style="text-align:center;">为诸如 <code>&lt;video&gt;</code> 和 <code>&lt;audio&gt;</code> 元素之类的媒介规定外部文本轨道</td></tr></tbody></table><h3 id="表单" tabindex="-1">表单 <a class="header-anchor" href="#表单" aria-hidden="true">#</a></h3><table><thead><tr><th style="text-align:center;">标签</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td style="text-align:center;"><code>&lt;datalist&gt;</code></td><td style="text-align:left;">定义选项列表。请与 input 元素配合使用该元素，来定义 input 可能的值</td></tr><tr><td style="text-align:center;"><code>&lt;keygen&gt;</code></td><td style="text-align:left;">规定用于表单的密钥对生成器字段</td></tr><tr><td style="text-align:center;"><code>&lt;output&gt;</code></td><td style="text-align:left;">定义不同类型的输出，比如脚本的输出</td></tr></tbody></table><h3 id="语义和结构" tabindex="-1">语义和结构 <a class="header-anchor" href="#语义和结构" aria-hidden="true">#</a></h3><table><thead><tr><th>标签</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td><code>&lt;header&gt;</code></td><td style="text-align:left;">定义了文档的头部区域</td></tr><tr><td><code>&lt;footer&gt;</code></td><td style="text-align:left;">定义 section 或 document 的页脚</td></tr><tr><td><code>&lt;article&gt;</code></td><td style="text-align:left;">定义页面独立的内容区域</td></tr><tr><td><code>&lt;section&gt;</code></td><td style="text-align:left;">定义文档中的节（section、区段）</td></tr><tr><td><code>&lt;aside&gt;</code></td><td style="text-align:left;">定义页面的侧边栏内容</td></tr><tr><td><code>&lt;nav&gt;</code></td><td style="text-align:left;">定义运行中的进度（进程）</td></tr></tbody></table>',10),c=[r];function n(i,o,s,h,g,x){return e(),d("div",null,c)}const u=t(a,[["render",n]]);export{_ as __pageData,u as default};
